package operators;

public class ArthimaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// arthimatic operator  + ,- ,*, /,%;
		
		//int a,b operand
		// a+b expression
		
		// addition 
		int a=5, b=10;
		System.out.println("Addition "+(a+b));
		
        //subtraction 
		
		int c=10, d=5;
		System.out.println("Subtraction"+(c-d));
		  
		// multiplication*
		 int e=5,f=10;
		 System.out.println("Multiplication"+(e*f));
		 
		 //Divided /
		 System.out.println("Division"+(e/f));
		 
		 // Modulus 
		 System.out.println("Modulus"+(e%f));
	}

}
